ALTER SESSION SET NLS_DATE_FORMAT = 'DD.MM.YYYY'
/
DROP TABLE CUSTOMERS
/
CREATE TABLE  CUSTOMERS (	
    CTMR_ID NUMBER, 
	CTMR_FRST_NAME VARCHAR2(30), 
	CTMR_LAST_NAME VARCHAR2(30), 
	CTMR_STREET_ADDRESS1 VARCHAR2(255), 
	CTMR_STREET_ADDRESS2 VARCHAR2(30), 
	CTMR_CITY VARCHAR2(30), 
	CTMR_STATE VARCHAR2(2), 
	CTMR_POSTAL_CODE NUMBER, 
	CTMR_EMAIL VARCHAR2(30), 
	CTMR_PHONE_NUMBER1 VARCHAR2(30), 
	CTMR_PHONE_NUMBER2 VARCHAR2(30), 
	CTMR_URL VARCHAR2(30), 
	CTMR_CREDIT_LIMIT NUMBER, 
	CTMR_TAGS VARCHAR2(30),
	CTMR_UPDATED_BY NUMBER,
	CTMR_UPDATED_DD DATE,	
	CONSTRAINT CUSTOMERS_PK PRIMARY KEY (CTMR_ID)
	USING INDEX  ENABLE
   )
/
DROP TABLE ORDERS
/
CREATE TABLE  ORDERS (	
	ORDR_ID NUMBER, 
	ORDR_CTMR_ID NUMBER, 
	ORDR_TOTAL NUMBER, 
	ORDR_DD DATE, 
	ORDR_USER_NAME VARCHAR2(30),
	ORDR_TAGS VARCHAR2(30), 
	ORDR_UPDATED_BY NUMBER,
	ORDR_UPDATED_DD DATE, 
	CONSTRAINT ORDERS_PK PRIMARY KEY (ORDR_ID)
    USING INDEX  ENABLE
   )
/
DROP TABLE  ORDER_ITEMS
/
CREATE TABLE  ORDER_ITEMS(	
	ORDR_ITEM_ID NUMBER, 
	ORDR_ITEM_ORDR_ID NUMBER, 
	ORDR_ITEM_PRDT_INFO_ID NUMBER, 
	ORDR_ITEM_UNIT_PRICE NUMBER, 
	ORDR_ITEM_QUANTITY NUMBER, 
	ORDR_ITEM_UPDATED_BY NUMBER,
	ORDR_ITEM_UPDATED_DD DATE, 
	CONSTRAINT ORDER_ITEMS_PK PRIMARY KEY (ORDR_ITEM_ID)
	USING INDEX  ENABLE
   )
/
DROP TABLE PRODUCT_INFO
/
CREATE TABLE  PRODUCT_INFO(
	PRDT_INFO_ID NUMBER, 
	PRDT_INFO_NAME VARCHAR2(30), 
	PRDT_INFO_DESCR VARCHAR2(255), 
	PRDT_INFO_CATEGORY VARCHAR2(30), 
	PRDT_INFO_AVAIL VARCHAR2(1), 
	PRDT_INFO_LIST_PRICE NUMBER, 
	PRDT_INFO_PRODUCT_IMAGE BLOB, 
	PRDT_INFO_MIMETYPE VARCHAR2(30), 
	PRDT_INFO_FILENAME VARCHAR2(30), 
	PRDT_INFO_IMAGE_LAST_UPDATE DATE, 
	PRDT_INFO_TAGS VARCHAR2(30), 
	PRDT_INFO_UPDATED_BY NUMBER,
	PRDT_INFO_UPDATED_DD DATE, 
	CONSTRAINT PRODUCT_INFO_PK PRIMARY KEY (PRDT_INFO_ID)
	USING INDEX  ENABLE
   )
/
DROP TABLE STATES
/
CREATE TABLE  STATES(
	STTS_ID NUMBER, 
	STTS_ST VARCHAR2(2), 
	STTS_STATE_NAME VARCHAR2(30), 
	STTS_UPDATED_BY NUMBER,
	STTS_UPDATED_DD DATE, 
	CONSTRAINT STATES_PK PRIMARY KEY (STTS_ID)
	USING INDEX  ENABLE
   )
/
DROP TABLE DEPARTMENTS
/
CREATE TABLE  DEPARTMENTS(
	DEPT_ID NUMBER, 
	DEPT_NAME VARCHAR2(50), 
	DEPT_LOCATION VARCHAR2(50), 
	DEPT_UPDATED_BY NUMBER,
	DEPT_UPDATED_DD DATE, 
	CONSTRAINT DEPT_PK PRIMARY KEY (DEPT_ID)
	USING INDEX  ENABLE
   )
