CREATE OR REPLACE TRIGGER  BI_CUSTOMERS 
  BEFORE INSERT ON CUSTOMERS              
  FOR EACH ROW 
BEGIN  
  IF :NEW.CTMR_ID IS NULL THEN
    SELECT CUSTOMERS_SEQ.NEXTVAL INTO :NEW.CTMR_ID FROM SYS.DUAL;
  END IF;
  :NEW.CTMR_UPDATED_DD := SYSDATE;
  :NEW.CTMR_UPDATED_BY := APEX_UTIL.GET_CURRENT_USER_ID;
END;
/
ALTER TRIGGER  BI_CUSTOMERS ENABLE
/
